import Container from 'react-bootstrap/Container';
import {Button, Stack} from "react-bootstrap";
import BudgetCard from './Components/BudgetCard';
import AddBudgetModal from './Components/AddBudgetModal';
import { useState } from 'react'; 
import { useBudgets } from './Contexts/BudgetContext'

function App() {
  const [showAddBudgetModal, setShowAddBudgetModal] = useState()
  const { budgets, expenses } = useBudgets() 

  return(
    <>
    <Container className="my-4"> 
    <Stack direction="horizontal" gap="2" className="mb-4"> 
      <h1 className="me-auto">Budgets</h1>
      <Button variant="primary" onClick={() => { 
        setShowAddBudgetModal(true) // When clicked, this will set the Modal to true, which will make it appear 
      }}>Add Budget</Button>
      <Button variant="outline-primary">Add Expense</Button>
    </Stack>
    <div style={{ 
      display: "grid", 
      gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))", 
      gap: "1rem",
      alignItems: "flex-start",  
    }}
    >
      {budgets.map(budget => { 
        const amount = getBudgetExpenses(budget.id).reduce((total, expense) => total + expense.amount, 0) // Get all of the expenses from the budget 
        return ( 
          <BudgetCard 
            key={budget.id} 
            name={budget.name} 
            amount={budget.amount} 
            max={budget.max}
          />
        ) 
        })}
    </div>
    </Container>
    <AddBudgetModal show={showAddBudgetModal} handleClose={() => { 
        setShowAddBudgetModal(false); {/* The "show" attribute will make this Modal show */}
    }} /> 
    </> 
  )
}

export default App;
